export default {

	getMapMarkers: () => {
		const markers = getRecentNearby.data.map((bird) => {
			return {
					"lat": utils.fuzzy(bird.lat),
					"long": utils.fuzzy(bird.lng),
					"title": bird.comName,
					"color": Locator.selectedMarker == bird? "#0000ff":"#ff0000"
				}
		})
		return markers.filter(x => (x.title))
	},

	defaultLocation: {
		lat: 39.925751,
		long: -83.806694,
		title: "",
		color: "#ff0000"
	},

	handleMarkerClick: async() => {
		const [ bird ] = getRecentNearby.data.filter((entry) => {
			return entry.comName === Locator.selectedMarker.title
		})
		await utils.setSelectedBird(bird)
	},

	handleCreateMarker: async() => {
		await storeValue("lat", Locator.selectedMarker.lat);
		await storeValue("long", Locator.selectedMarker.long);
		getRecentNearby.run()
	}
}
